import React, { useState } from "react";
import uniqid from "uniqid";

import Main from "./components/Main";
import Navigation from "./components/Navigation";
import FormTask from "./components/FormTask";

const App = () => {
  const [tasksList, setTasksList] = useState([]);
  const [isAddTaskOpen, setIsAddTaskOpen] = useState(false);
  const [isMobileNavOpen, setIsMobileNavOpen] = useState(false);
  const [mode, setMode] = useState("add");
  const [selectedTask, setSelectedTask] = useState(null);

  const handleAddTaskOpen = () => {
    setIsAddTaskOpen(true);
  };

  const handleCancelAddTaskOpen = () => {
    setIsAddTaskOpen(false);
  };

  const handleIsMobileNavOpen = () => {
    setIsMobileNavOpen(true);
  };

  const handleMobileNavClose = () => {
    setIsMobileNavOpen(false);
  };

  const handleTaskAdd = () => {
    setMode("add");
    setSelectedTask(null);
    setIsAddTaskOpen(true);
  };

  const handleFormSubmit = (
    id,
    title,
    date,
    assignee,
    description,
    link,
    tags,
    columns,
    comments
  ) => {
    event.preventDefault();
    const newTask = {
      id: uniqid(),
      title,
      date,
      assignee,
      description,
      link,
      tags,
      columns,
      comments,
    };
    setTasksList([...tasksList, newTask]);
    handleCancelAddTaskOpen();
  };

  const handleTaskSelect = (clickedTask) => {
    setMode("edit");
    setSelectedTask(clickedTask);
    setIsAddTaskOpen(true);
  };

  console.log(mode, selectedTask, isAddTaskOpen);

  const handleTaskEdit = (
    event,
    title,
    date,
    assignee,
    description,
    link,
    tags,
    columns,
    comments
  ) => {
    event.preventDefault();
    const editedTask = {
      title,
      date,
      assignee,
      description,
      link,
      tags,
      columns,
      comments,
    };

    const editedTaskList = tasksList.map((taskPost) => {
      if (taskPost.id === editedTask.id) {
        return editedTask;
      } else {
        return taskPost;
      }
    });

    setTasksList(editedTaskList);
  };

  return (
    <>
      <div className="header">
        <Navigation
          isMobileNavOpen={isMobileNavOpen}
          handleMobileNavClose={handleMobileNavClose}
        />
        {isAddTaskOpen && (
          <FormTask
            mode={mode}
            handleFormSubmit={
              mode === "add" ? handleFormSubmit : handleTaskEdit
            }
            CancelAddTaskOpen={handleCancelAddTaskOpen}
            taskToEdit={selectedTask}
          />
        )}
        <Main
          addTaskOpen={handleTaskAdd}
          openMobileNav={handleIsMobileNavOpen}
          tasksList={tasksList}
          onTaskSelect={handleTaskSelect}
        />
      </div>
    </>
  );
};

export default App;
